import StarRatingComponent from 'react-star-rating-component';
import React from 'react';
import './Rating.css';
import Comments from './Comments';

class Rating extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      email: '',
      message: '',
      rating: 0,
      storage: JSON.parse(localStorage.getItem('comments')),
    };
  }

  componentDidMount() {
    const { storage } = this.state;
    if (storage === null) {
      this.setState({ storage: [] });
    }
  }

  componentDidUpdate() {
    const { storage } = this.state;
    localStorage.setItem('comments', [JSON.stringify(storage)]);
  }

  onStarClick(event) {
    console.log(event)

  }

  updateField(field, event) {
    this.setState({ [field]: event.target.value });
  }

  createComment() {
    const { email, message, rating, storage } = this.state;
    this.setState({
      email: '',
      message: '',
      rating: 0,
      storage: [...storage, { email, message, rating }],
    });
  }

  renderEmail() {
    const { email } = this.state;
    return (
      <div className="rating-section">
        <input
          id="email"
          placeholder="E-mail"
          value={email}
          onChange={(event) => this.updateField('email', event)}
        />
      </div>
    );
  }

  renderTextArea() {
    const { message } = this.state;
    return (
      <textarea
        placeholder="Mensagem"
        data-testid="product-detail-evaluation"
        value={message}
        onChange={(event) => this.updateField('message', event)}
      />
    );
  }

  render() {
    const { storage, rating } = this.state;
    return (
      <div>
        <form>
          <h2>Deixe sua Avaliação</h2>
          <StarRatingComponent
            name="rate1"
            className="stars"
            starCount={5}
            value={rating}
            onStarClick={(e) => this.onStarClick(e)}
          />
          {this.renderEmail()}
          {this.renderTextArea()}
          <button type="button" className="rating-button" onClick={() => this.createComment()}>
            Avaliar
          </button>
        </form>
        <div>
          <Comments storage={storage} />
        </div>
      </div>
    );
  }
}

export default Rating;
