import React from 'react';
import './SearchBar.css';
import ProductList from './ProductList';
import Categories from './Categories';
import ButtonCart from './ButtonCart';
// import CartPage from './CartPage';

class SearchBar extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      item: '',
      category: '',
      search: false,
      searchCategory: false,
      total: 0,
    };
    this.changeValue = this.changeValue.bind(this);
  }

  getCategorie(category) {
    this.setState({
      category,
      search: false,
      searchCategory: true,
    });
  }

  doSearch() {
    this.setState({
      search: true,
      searchCategory: false,
    });
  }

  addText() {
    const { item } = this.state;
    if (item === '') {
      return (
        <p data-testid="home-initial-message" className="texto-pesquisa">
          Digite algum termo de pesquisa ou escolha uma categoria.
        </p>
      );
    }
    return <p className="texto-pesquisa-null">{item}</p>;
  }

  changeValue(e) {
    this.setState({
      item: e.target.value,
      search: false,
    });
  }

  render() {
    const { item, search, searchCategory, category, total } = this.state;
    let storage = JSON.parse(localStorage.getItem('cart'));
    if (storage === null) {
      storage = [];
    };
    const total2 = storage.reduce((sum, e) => (sum + e.total), 0);
    this.setState(()=>{total: total2})
    return (
      <div>
        <div className="searchbar">
          <center>
            <input
              data-testid="query-input"
              type="text"
              className="input"
              onChange={this.changeValue}
            />
            {this.addText()}
          </center>
          <button
            data-testid="query-button"
            type="button"
            onClick={() => this.doSearch()}
          >
            PESQUISAR
          </button>
          <ButtonCart total={total} />
        </div>
        <div className="product-sidebar">
          <Categories click={(event) => this.getCategorie(event)} />
          {/* <CartPage /> */}
          {searchCategory && <ProductList category={category} />}
          {search && <ProductList item={item} />}
        </div>
        <br />
      </div>
    );
  }
}

export default SearchBar;
