import React from 'react';
import './Rating.css';
import './Comments.css';

class Comments extends React.Component {

  renderComments() {
    const { storage } = this.props;
    return (
      storage && storage.map((e) => (
        <div className="comment" key={em}>
          <p>
            Email:
            {e.email}
          </p>
          <p>
            Mensagem:
            {e.message}
          </p>
          <p>
            Nota:
            {e.value}
          </p>
        </div>
      )));
  }

  render() {
    const { storage } = this.props;
    if (storage.length === 0) return (<h2>Sem comentários</h2>);
    return (
      <div className="comment-box">
        <h1>Comentários</h1>
        <div data-testid="product-detail-evaluation">
          {this.renderComments()}
        </div>
      </div>
    );
  }
}

export default Comments;
