import React from 'react';
import * as api from '../services/api';


class ProductList extends React.Component {
  constructor(props) {
    super(props);
    this.state = { categories: [] };
  }

  componentDidMount() {
    api.getCategories()
      .then((result) => this.setState({ categories: result }));
  }

  render() {

  }
}

export default ProductList;
