import React from 'react';
import './CartPage.css';

function removeProduct(item) {
  const storage = JSON.parse(localStorage.getItem('cart'));
  const newStorage = storage.filter((e) => e.title !== item);
  localStorage.setItem('cart', JSON.stringify(newStorage));
  window.location.reload();
}
class CartItem extends React.Component {
  constructor(props) {
    super(props);
    const { total } = this.props;
    this.state = { total };
  }

  decrease(item) {
    const { total } = this.state;
    if (total > 0) {
      this.setState((state) => ({ total: state.total - 1 }));
    }
    const storage = JSON.parse(localStorage.getItem('cart'));
    storage.map((e) => {
      if (e.title === item) { e.total -= 1; }
      });

  }

  increase() {
    const { quantity } = this.props;
    const { total } = this.state;
    if (total < quantity) {
      this.setState((state) => ({ total: state.total + 1 }));
    }
  }

  render() {
    const { total } = this.state;
    const { title, thumbnail, price } = this.props;
    return (
      <div className="cart-item">
        <img src={thumbnail} alt={title} />
        <p data-testid="shopping-cart-product-name">{title}</p>
        <div className="increase">
          <button
            type="button"
            data-testid="product-decrease-quantity"
            onClick={() => this.decrease(title)}
          >
            -
          </button>
          <p data-testid="shopping-cart-product-quantity">{total}</p>
          <button
            type="button"
            data-testid="product-increase-quantity"
            onClick={() => this.increase()}
          >
            +
          </button>
        </div>
        <button type="button" onClick={() => removeProduct(title)}>Remover Produto</button>
        <div>{`Valor: R$${price}`}</div>
      </div>
    );
  }
}

export default CartItem;
