import React from 'react';
import { Link } from 'react-router-dom';
import './CartPage.css';

class CartItem extends React.Component {
  constructor(props) {
    super(props);
    const { total } = this.props;
    this.state = { total };
  }

  render() {
    const { total } = this.state;
    const { title, thumbnail, price } = this.props;
    return (
      <div>
        <img src={thumbnail} alt={title} />
        <p data-testid="shopping-cart-product-name">{e.title}</p>
        <button type="button" onClick={() => this.decrease(e.total)}>-</button>
        <p data-testid="shopping-cart-product-quantity">{e.total}</p>
        <button type="button" onClick={() => this.increase()}>+</button>
        <div>{`Valor: R$${e.price}`}</div>
      </div>
    );
  }
}

export default CartItem;
