import React from 'react';

class Increment extends React.Component {
  constructor(props) {
    super(props);
    this.state = { quantity: 0 };
  }

  decrease() {
    const { quantity } = this.state;
    if (quantity > 0) {
      this.setState((state) => ({ quantity: state.quantity - 1 }));
    }
  }

  increase() {
    const { total } = this.props;
    const { quantity } = this.state;
    if (quantity < quantity) {
      this.setState((state) => ({ quantity: state.quantity + 1 }));
    }
  }

  addToCart() {
    const { quantity } = this.state;
    const { info } = this.props;
    const infoWithQuantity = { ...info, quantity };
    let storage = JSON.parse(localStorage.getItem('cart'));
    if (storage === null) {
      storage = [];
    }
    const newStorage = JSON.stringify([...storage, infoWithQuantity]);
    localStorage.setItem('cart', newStorage);
  }

  render() {
    const { quantity } = this.state;
    return (
      <div>
        <h1>Quantidade</h1>
        <button type="button" onClick={() => this.decrease()}>-</button>
        {quantity}
        <button type="button" onClick={() => this.increase()}>+</button>
        <button type="button" onClick={() => this.addToCart()}>Adicionar ao Carrinho</button>
      </div>
    );
  }
}

export default Increment;
