import React from 'react';
import * as api from '../services/api';
import './Categories.css';
import ProductList from './ProductList';


class Categories extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      categories: [],
      products: [],
      showProducts: false,
    };
  }

  componentDidMount() {
    api.getCategories()
      .then((result) => this.setState({
        categories: result,
        showProducts: false,
      }));
  }

  searchCatergory(event) {
    api.getProductsFromCategoryAndQuery(event, '')
      .then((result) => {
        console.log(result)
        this.setState({
          products: result.results,
          showProducts: true,
        });
      });
  }

  componentDidUpdate() {
    api.getCategories()
      .then((result) => this.setState({
        categories: result,
        showProducts: false,
      }));
  }

  render() {
    const { categories, products, showProducts } = this.state;
    return (
      <div className="categories">
        <h3>Categorias</h3>
        {categories.map((category) => (
          <button
            type="button"
            data-testid="category"
            key={category.id}
            value={category.name}
            onClick={() => this.searchCatergory(category.id)}
          >
            {category.name}
          </button>
        ))}
        {showProducts && <ProductList produtcs={products} />}
      </div>
    );
  }
}

export default Categories;
