import React from 'react';
import { Link } from 'react-router-dom';
import './Checkout.css';


class Checkout extends React.Component {
  constructor(props) {
    super(props);
    this.state = { total: 0 };
  }

  componentDidMount() {
    const storage = JSON.parse(localStorage.getItem('cart'));
    const FinalValue = (storage.reduce((sum, e) => (sum + e.total * e.price), 0).toFixed(2));
    this.setState({ total: FinalValue });
  }

  calculateTotal(total, price) {
    const finalValue = total * price;
    this.setState((state) => ({ total: state.total + finalValue }));
  }


  render() {
    const storage = JSON.parse(localStorage.getItem('cart'));
    const { total } = this.state;
    return (
      <div>
        <div className="list">
          <div className="list-child">
            {storage.map((e) => (
              <div key={e.title}>
                <div>
                  <span>{e.title}</span>
                  <span>{e.total}</span>
                  <span>{e.price}</span>
                </div>
              </div>
            ))}

          </div>
          <div className="total">
            <span>Total</span>
            <span>{total}</span>
          </div>
        </div>
        render
      </div>
    );
  }
}

export default Checkout;
