import React from 'react';
import './SearchBar.css';
import ProductList from './ProductList';
import Categories from './Categories';
import ButtonCart from './ButtonCart';
// import CartPage from './CartPage';

class SearchBar extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      item: '',
      category: '',
      search: false,
      searchCategory: false,
    };
    this.changeValue = this.changeValue.bind(this);
  }

  changeValue(e) {
    this.setState({
      item: e.target.value,
      search: false,
    });
  }

  addText() {
    const { item } = this.state;
    if (item === '') {
      return (
        <p data-testid="home-initial-message" className="texto-pesquisa">
          Digite algum termo de pesquisa ou escolha uma categoria.
        </p>
      );
    }
    return <p className="texto-pesquisa-null">{item}</p>;
  }

  doSearch() {
    this.setState({ search: true });
  }

  getCategorie() {
    this.setState({ searchCategory: true });
  }

  render() {
    const { item, search, searchCategory, category } = this.state;
    return (
      <div>
        <div className="searchbar">
          <center>
            <input
              data-testid="query-input"
              type="text"
              className="input"
              onChange={this.changeValue}
            />
            {this.addText()}
          </center>
          <button
            data-testid="query-button"
            type="button"
            onClick={() => this.doSearch()}
          >
            PESQUISAR
          </button>
          <ButtonCart />
        </div>
        <div className="product-sidebar">
          <Categories onCLick={() => this.searchCategory()} click={() => this.getCategorie()}/>
          {/* <CartPage /> */}
          {searchCategory && <ProductList products={category} />}
          {search && <ProductList products={item} />}
        </div>
        <br />
      </div>
    );
  }
}

export default SearchBar;
