import React from 'react';
import './Rating.css';
import Comments from './Comments';


class Rating extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      email: '',
      message: '',
      value: '',
      storage: JSON.parse(localStorage.getItem('comments')),
    };
    this.updateField = this.updateField.bind(this);
  }

  componentDidMount() {
    const { storage } = this.state;
    if (storage === null) {
      this.setState({ storage: [] });
    }
  }

  componentDidUpdate() {
    const { storage } = this.state;
    localStorage.setItem('comments', [JSON.stringify(storage)]);
  }

  updateField(field, event) {
    this.setState({ [field]: event.target.value });
  }

  updateRate(e) {
    this.setState({ value: e.target.value });
  }

  createComment() {
    const { email, message, value, storage } = this.state;
    const comment = { email, message, value };
    this.setState({
      email: '',
      message: '',
      value: '',
      storage: [...storage, comment],
    });
  }

  render() {
    const { email, message, storage } = this.state;
    return (
      <div>
        <form>
          <div className="rating-section">
            <input id="email" placeholder="E-mail" value={email} onChange={(event) => this.updateField('email', event)} />
            <label htmlFor="1">
1
              <input type="radio" name="rate" id="1" value="1" onChange={(e) => this.updateRate(e)} />
</label>

            <label htmlFor="2">2</label>
            <input type="radio" name="rate" id="2" value="2" onChange={(e) => this.updateRate(e)} />
            <label htmlFor="3">3</label>
            <input type="radio" name="rate" id="3" value="3" onChange={(e) => this.updateRate(e)} />
            <label htmlFor="4">4</label>
            <input type="radio" name="rate" id="4" value="4" onChange={(e) => this.updateRate(e)} />
            <label htmlFor="5">5</label>
            <input type="radio" name="rate" id="5" value="5" onChange={(e) => this.updateRate(e)} />
          </div>
          <textarea placeholder="Mensagem" value={message} onChange={(event) => this.updateField('message', event)} />
          <button type="button" className="rating-button" onClick={() => this.createComment()}>Avaliar</button>
        </form>
        <div>
          <Comments storage={storage} />
        </div>
      </div>
    );
  }
}

export default Rating;
