import React from 'react';
import { getCategories } from '../services/api';


class Categories extends React.Component {
  constructor(props) {
    super(props);
    this.state = { categories: [] };
  }

  componentDidMount() {
    getCategories()
      .then((result) => this.setState({ categories: result }));
  }

  render() {
    return (
      <ul>
        {this.state}
      </ul>
    );
  }
}


export default Categories;
