import React from 'react';
import api from '../services/api';


class Categories extends React.Component {
  constructor(props) {
    super(props);
    this.state = { categories: [] };
  }

  componentDidMount() {
   api.getCategories()
   .then(result => this.setState)
  }

  render() {
    return (
      <div>
        {this.state.categories}
      </div>
    );
  }
}


export default Categories;
