import React from 'react';
import './Rating.css';


class Rating extends React.Component {

  constructor(props) {
    super(props);
    this.state = {
      comments: localStorage.getItem('comments'),
      email: '',
      message: '',
      checked: false,
      value: '',
    };
    this.updateField = this.updateField.bind(this);
  }

  updateField(field, event) {
    this.setState({ [field]: event.target.value });
  }

  updateRate() {
    this.setState({ 
      checked: true,
     });
  }

  render() {
    const { comments, email, message, checked, value } = this.state;
    return (
      <div>
        <form>
          <div className="rating-section">
            <input id="email" placeholder="E-mail" value={email} onChange={(event) => this.updateField('email', event)} />
            <label htmlFor="1">{value}</label>
            <input type="radio" name="rate" id="1" value="1" checked={checked} onChange={() => this.updateRate()} />
            <label htmlFor="2">2</label>
            <input type="radio" name="rate" id="2" value="2" checked={checked} onChange={() => this.updateRate()} />
            <label htmlFor="1">3</label>
            <input type="radio" name="rate" id="3" value="3" checked={checked} onChange={() => this.updateRate()} />
            <label htmlFor="1">4</label>
            <input type="radio" name="rate" id="4" value="4" checked={checked} onChange={() => this.updateRate()} />
            <label htmlFor="1">5</label>
            <input type="radio" name="rate" id="5" value="5" checked={checked} onChange={() => this.updateRate()} />
          </div>
          <textarea placeholder="Mensagem" value={message} onChange={(event) => this.updateField('message', event)} />
          <button type="button" className="rating-button">Avaliar</button>
        </form>
        <div>
          <h1>Comentários</h1>
          {comments}
        </div>
      </div>
    );
  }
}

export default Rating;
