const myObject = { method: 'GET', headers: new Headers() };
export async function getCategories() {
  await fetch('https://api.mercadolibre.com/sites/MLB/categories', myObject)
    .then((response) => response.json());
}
export async function getProductsFromCategoryAndQuery(categoryId, query) {
  await fetch(`https://api.mercadolibre.com/sites/MLB/search?category=${categoryId}&q=${query}`, myObject)
    .then((response) => response.json());
}
