import React from 'react';
import './Rating.css';


class Rating extends React.Component {

  constructor(props) {
    super(props);
    this.state = {
      comments: localStorage.getItem('comments'),
      email: '',
      // rating: '',
      message: '',
    };
    this.updateField = this.updateField.bind(this)
  }

  updateField(field, event) {
    this.setState({ [field]: event.target.value });
  }

  render() {
    const { comments, email } = this.state;
    return (
      <div>
        <form>
          <div className="rating-section">
            <input id="email" placeholder="E-mail" value={email} onChange={(event) => updateField('email', event)}/>
            <label htmlFor="1">1</label>
            <input type="radio" name="rate" id="1" value="1" />
            <label htmlFor="2">2</label>
            <input type="radio" name="rate" id="2" value="2" />
            <label htmlFor="1">3</label>
            <input type="radio" name="rate" id="3" value="3" />
            <label htmlFor="1">4</label>
            <input type="radio" name="rate" id="4" value="4" />
            <label htmlFor="1">5</label>
            <input type="radio" name="rate" id="5" value="5" />
          </div>
          <textarea placeholder="Mensagem" />
          <button type="button" className="rating-button" onClick={() => saveComment()}>Avaliar</button>
        </form>
        <div>
          <h1>Coment√°rios</h1>
          {comments}
        </div>
      </div>
    );
  }
}

export default Rating;
