import React from 'react';
import { Link } from 'react-router-dom';
import './CartPage.css';

class CartPage extends React.Component {
  constructor(props) {
    super(props);
    const { nome, total, valor } = this.props;
    this.state = {
      nome, 
      total, valor
    };
  }

  render() {
    const { arrayProducts } = this.state;
    if (arrayProducts && (arrayProducts.length !== 0)) {
      return (
        <div>
          <h2 className="h2-fixo">Carrinho de compras: </h2>
          <div className="lista-pai">
            {arrayProducts.map((e) => (
              <div key={e.id} className="sub-lista">
                <img src={e.thumbnail} alt={e.title} />
                <p data-testid="shopping-cart-product-name">{e.title}</p>
                <button type="button" onClick={() => this.decrease(e.total)}>-</button>
                <p data-testid="shopping-cart-product-quantity">{e.total}</p>
                <button type="button" onClick={() => this.increase()}>+</button>
                <div>{`Valor: R$${e.price}`}</div>
              </div>
            ),
            )}
          </div>
          <Link to="/"><button type="button">VOLTAR</button></Link>
        </div>
      );
    }
    return (
      <div>
        <div className="empty_content" data-testid="shopping-cart-empty-message">
          Seu carrinho est√° vazio
        </div>
      </div>
    );
  }
}

export default CartPage;
