import React from 'react';
import { Link } from 'react-router-dom';
import './Checkout.css';


class Checkout extends React.Component {
  constructor(props) {
    super(props);
    this.state = { total: 0 };
  }

  componentDidMount() {
    const storage = JSON.parse(localStorage.getItem('cart'));
    const FinalValue = storage.reduce((sum, e) => (sum + e.total * e.price));
    this.setState({ total: FinalValue });
  }

  calculateTotal(total, price) {
    const finalValue = total * price;
    this.setState((state) => ({ total: state.total + finalValue }));
  }


  render() {
    const storage = JSON.parse(localStorage.getItem('cart'));
    return (
      <div>
        {storage.map((e) => (
          <div>
            <div className="list">
              <span>{e.title}</span>
              <span>{e.total}</span>
              <span>{e.price}</span>
            </div>
            <div>
              <span>Total</span>
              <span>{() => this.calculateTotal(e.total, e.price)}</span>
              <span>{this.state.total}</span>
            </div>
          </div>
        ))}
      </div>
    );
  }
}

export default Checkout;
